$(function(){$("#GetToken").click(function(){$.ajax({type:"POST",url:"/Login",data:JSON.stringify({Username:$("#login").val(),Password:$("#pass").val()}),contentType:"application/json"}).done(function(n){console.log(n);location.href="/Home/About"}).fail(function(n,t,i){console.log(i)})});$("#UseToken").click(function(){$.ajax({method:"GET",url:"/Home/About",beforeSend:function(n){n.setRequestHeader("Authorization","Bearer "+$("#token").text())}}).done(function(n){$("#result").text(JSON.stringify(n))}).fail(function(n){$("#result").text(JSON.stringify(n))})})});